import './App.css';

import ChatBot from 'react-simple-chatbot';
import React from 'react';
import  FloatButton from './Botao';
import { ThemeProvider } from 'styled-components';
  const Tema = {
    background: '#f5f8fb',
    fontFamily: 'Verdana',
    headerBgColor: '#6e48aa',
    headerFontColor: '#fff',
    headerFontSize: '16px',
    botBubbleColor: '#6E48AA',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a'
  };

  const steps = [
        {
          id: '0',
          message: 'Welcome to react chatbot!',
          trigger: '1',
        },
        {
          id: '1',
          user: true,
          message: 'Usuario',
          trigger: '2',
        },      
        {
          id: '2',
          message: 'Bye!',
          end: true,
        },
      ];

 const App  = () => (
  <ThemeProvider theme={Tema}>
    <React.StrictMode>
    <ChatBot steps={steps} floating = {true}/>
    </React.StrictMode>
    </ThemeProvider>
 );
 export default App;
